// Apply template styles to news title
export const applyTemplateNewsTitleStyles = (template) => {
  let styles = {
    color: template.accentColor,
    fontFamily: template.titleFont,
    fontWeight: 'bold',
    position: 'relative'
  };
  
  if (template.newsItemStyle === 'glowing') {
    styles = {
      ...styles,
      textShadow: `0 0 5px ${template.accentColor}80, 0 0 10px ${template.accentColor}40`
    };
  }

  if (template.itemCounter && template.id === 'corporate') {
    styles = {
      ...styles,
      position: 'relative',
      paddingLeft: '28px',
      '&::before': {
        content: 'attr(data-count)',
        position: 'absolute',
        left: 0,
        top: '2px',
        width: '22px',
        height: '22px',
        backgroundColor: template.accentColor,
        color: 'white',
        borderRadius: '50%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '12px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
      }
    };
  }

  if (template.goldAccents && template.id === 'classic') {
    styles = {
      ...styles,
      backgroundImage: `linear-gradient(45deg, ${template.accentColor}, #63b3ed)`,
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      display: 'inline-block',
      position: 'relative',
      paddingBottom: '3px',
      '&::after': {
        content: '""',
        position: 'absolute',
        left: '0',
        bottom: '0',
        width: '50%',
        height: '2px',
        background: `linear-gradient(to right, ${template.accentColor}, transparent)`
      }
    };
  }

  if (template.neonAccents && template.id === 'dark') {
    styles = {
      ...styles,
      color: template.accentColor,
      textShadow: `0 0 3px ${template.accentColor}80, 0 0 5px ${template.accentColor}40`,
      letterSpacing: '1px'
    };
  }

  if (template.classicSerif && template.id === 'elegant') {
    styles = {
      ...styles,
      fontFamily: "'Noto Serif SC', serif",
      fontWeight: '600',
      letterSpacing: '0.5px',
      position: 'relative',
      display: 'inline-block',
      '&::after': {
        content: '""',
        position: 'absolute',
        left: '0',
        bottom: '-3px',
        width: '100%',
        height: '1px',
        background: `${template.accentColor}50`
      }
    };
  }

  if (template.warmGlow && template.id === 'warm') {
    styles = {
      ...styles,
      position: 'relative',
      display: 'inline-block',
      '&::before': {
        content: '""',
        position: 'absolute',
        left: '-5px',
        top: '50%',
        transform: 'translateY(-50%)',
        width: '3px',
        height: '70%',
        background: template.accentColor,
        borderRadius: '3px'
      }
    };
  }

  if (template.organicShapes && template.id === 'nature') {
    styles = {
      ...styles,
      position: 'relative',
      paddingLeft: '5px',
      borderBottom: `1px solid ${template.accentColor}30`,
      paddingBottom: '3px',
      display: 'inline-block',
      '&::before': {
        content: '""',
        position: 'absolute',
        left: '-3px',
        top: '0',
        width: '3px',
        height: '100%',
        background: template.accentColor,
        borderRadius: '3px'
      }
    };
  }

  if (template.terminalStyle && template.id === 'techGeek') {
    styles = {
      ...styles,
      fontFamily: "'Source Code Pro', monospace, 'Noto Sans SC', sans-serif",
      letterSpacing: '1px',
      color: template.accentColor,
      '&::before': {
        content: '"$ "',
        color: template.accentColor,
        opacity: '0.8'
      }
    };
  }
  
  if (template.holographicEffect && template.id === 'futuristic') {
    styles = {
      ...styles,
      color: template.accentColor,
      fontWeight: '700',
      letterSpacing: '0.5px',
      position: 'relative',
      padding: '2px 4px',
      background: template.improvedContrast ? 'rgba(0, 0, 0, 0.6)' : 'transparent',
      borderRadius: '2px',
      border: `1px solid ${template.accentColor}30`,
      boxShadow: `0 0 3px ${template.accentColor}40`,
      textShadow: `0 0 1px ${template.accentColor}, 0 0 2px rgba(0, 0, 0, 0.8)`
    };
  }
  
  if (template.boldTypography && template.id === 'minimalist') {
    styles = {
      ...styles,
      color: '#000000',
      fontWeight: '900',
      fontSize: '120%',
      letterSpacing: '-0.03em',
      textTransform: 'uppercase',
      position: 'relative',
      paddingBottom: '5px',
      '&::after': {
        content: '""',
        position: 'absolute',
        left: '0',
        bottom: '0',
        width: '30px',
        height: '3px',
        background: '#000000'
      }
    };
  }

  if (template.id === 'fieryRed' && template.highContrast) {
    styles = {
      ...styles,
      color: '#dc2626', // 更热烈的红色
      fontWeight: '700',
      fontSize: '110%',
      letterSpacing: '0.5px',
      position: 'relative',
      paddingLeft: '10px',
      paddingBottom: '5px',
      textShadow: '0 1px 3px rgba(0,0,0,0.3)',
      '&::before': {
        content: '""',
        position: 'absolute',
        left: '0',
        top: '0',
        width: '3px',
        height: '100%',
        background: '#dc2626', // 更热烈的红色
        borderRadius: '2px'
      }
    };
  }

  return styles;
};

// Apply template styles to news content
